query GetIssueOrPRCore($owner: String!, $name: String!, $number: Int!) {
  repository(owner: $owner, name: $name) {
    issueOrPullRequest(number: $number) {
      __typename
      ... on Issue {
        id
        databaseId
        number
        url
        title
        body
        state
        locked
        author {
          __typename
          login
          url
          avatarUrl
          ... on User { id databaseId }
          ... on Organization { id databaseId }
          ... on Bot { id databaseId }
          ... on Mannequin { id databaseId }
        }
        authorAssociation
        createdAt
        updatedAt
        closedAt
        labels(first: 100) { nodes { name color description } }
        milestone { title description dueOn state number }
        assignees(first: 100) { nodes { login id databaseId url avatarUrl __typename } }
        comments { totalCount }
      }
      ... on PullRequest {
        id
        databaseId
        number
        url
        title
        body
        state
        isDraft
        locked
        author {
          __typename
          login
          url
          avatarUrl
          ... on User { id databaseId }
          ... on Organization { id databaseId }
          ... on Bot { id databaseId }
          ... on Mannequin { id databaseId }
        }
        authorAssociation
        createdAt
        updatedAt
        closedAt
        mergedAt
        mergedBy {
          __typename
          login
          url
          avatarUrl
          ... on User { id databaseId }
          ... on Organization { id databaseId }
          ... on Bot { id databaseId }
          ... on Mannequin { id databaseId }
        }
        mergeCommit { oid url }
        baseRefName
        headRefName
        headRefOid
        additions
        deletions
        changedFiles
        labels(first: 100) { nodes { name color description } }
        milestone { title description dueOn state number }
        assignees(first: 100) { nodes { login id databaseId url avatarUrl __typename } }
        comments { totalCount }
        reviews { totalCount }
        files { totalCount }
      }
    }
  }
}
